import React, { useState, useEffect } from 'react';
import '../styles/Actions.css';

const Actions = ({ onAddAction }) => {
  const [actions, setActions] = useState([]);
  const [type, setType] = useState('');
  const [description, setDescription] = useState('');
  const [date, setDate] = useState('');
  const [speciesId, setSpeciesId] = useState('');
  const [message, setMessage] = useState(''); // To display success/error messages

  useEffect(() => {
    fetchActions();
  }, []);

  const fetchActions = async () => {
    try {
      const response = await fetch('http://localhost:5000/Actions_API/actions');
      if (response.ok) {
        const data = await response.json();
        setActions(data);
      } else {
        const errorText = await response.text();
        setMessage(`Failed to fetch actions. Server returned: ${errorText}`);
      }
    } catch (error) {
      console.error('Error fetching actions:', error);
      setMessage('An error occurred while fetching actions.');
    }
  };

  const handleAddAction = async (e) => {
    e.preventDefault();
    try {
      const payload = { type, description, date, species_id: speciesId };
      const response = await fetch('http://localhost:5000/Actions_API/actions', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(payload),
      });

      if (response.ok) {
        const newAction = await response.json();
        setActions([...actions, newAction]);
        setMessage('Action added successfully!');
        onAddAction(newAction); // Optionally, update parent state

        // Reset form fields
        setType('');
        setDescription('');
        setDate('');
        setSpeciesId('');
      } else {
        const errorText = await response.text();
        setMessage(`Failed to add action. Server returned: ${errorText}`);
      }
    } catch (error) {
      console.error('Error adding action:', error);
      setMessage('An error occurred while adding the action.');
    }
  };

  return (
    <div className="actions-container">
      <div className="image-container">
        <img src="/assets/ark.avif" alt="Wildlife" className="sidebar-image" />
      </div>

      <div className="actions-content">
        <h2>Actions</h2>
        {message && <p className="message">{message}</p>}

        <ul className="actions-list">
          {actions.map((action) => (
            <li key={action.id} className="action-item">
              {action.type} - {action.description} (Date: {action.date}, Species ID: {action.species_id})
            </li>
          ))}
        </ul>

        <form onSubmit={handleAddAction} className="add-action-form">
          <input
            type="text"
            placeholder="Type"
            value={type}
            onChange={(e) => setType(e.target.value)}
            required
            className="form-input"
          />
          <input
            type="text"
            placeholder="Description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
            className="form-input"
          />
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            required
            className="form-input"
          />
          <input
            type="number"
            placeholder="Species ID"
            value={speciesId}
            onChange={(e) => setSpeciesId(e.target.value)}
            required
            className="form-input"
          />
          <button type="submit" className="form-button">Add Action</button>
        </form>
      </div>
    </div>
  );
};

export default Actions;
