import React, { useState, useEffect } from 'react';
import SpeciesCard from '../components/SpeciesCard';
import '../styles/Learn.css';

const Learn = () => {
  const [speciesList, setSpeciesList] = useState([]); // Initialize as an empty array

  useEffect(() => {
    const fetchSpecies = async () => {
      try {
        const response = await fetch('http://localhost:5000/WildLife_API/species');
        if (!response.ok) {
          throw new Error(`Error: ${response.statusText}`);
        }
        const data = await response.json();
        setSpeciesList(data); // Ensure data is set correctly
        console.log(data);
      } catch (error) {
        console.error('Failed to fetch species:', error);
      }
    };

    fetchSpecies();
  }, []);

  return (
    <div className="learn-container">
      <h1 className="learn-title">Learn About Species</h1>
      <p className="learn-description">
        Discover endangered species, their habitats, and the threats they face. Take action to protect our planetâ€™s biodiversity.
      </p>
      <div className="species-grid">
        
          speciesList.map((species) => (
            <SpeciesCard key={species.id} species={species} />
          ))
  
      </div>
    </div>
  );
};

export default Learn;
