import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/Actions.css';

const Actions = () => {
  const [actions, setActions] = useState([]);
  const [type, setType] = useState('');
  const [description, setDescription] = useState('');
  const [date, setDate] = useState('');
  const [speciesId, setSpeciesId] = useState('');

  useEffect(() => {
    fetchActions();
  }, []);

  const fetchActions = async () => {
    try {
      const response = await axios.get('/Actions_API/actions');
      setActions(response.data);
    } catch (error) {
      console.error('Error fetching actions:', error);
    }
  };

  const handleAddAction = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/Actions_API/actions', { type, description, date, species_id: speciesId });
      setActions([...actions, response.data]);
      setType('');
      setDescription('');
      setDate('');
      setSpeciesId('');
    } catch (error) {
      console.error('Error adding action:', error);
    }
  };

  return (
    <div>
      <h1>Actions</h1>
      <ul>
        {actions.map((action) => (
          <li key={action.id}>
            {action.type} - {action.description} (Date: {action.date}, Species ID: {action.species_id})
          </li>
        ))}
      </ul>
      <form onSubmit={handleAddAction}>
        <input
          type="text"
          placeholder="Type"
          value={type}
          onChange={(e) => setType(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
        />
        <input
          type="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
          required
        />
        <input
          type="number"
          placeholder="Species ID"
          value={speciesId}
          onChange={(e) => setSpeciesId(e.target.value)}
          required
        />
        <button type="submit">Add Action</button>
      </form>
    </div>
  );
};

export default Actions;
