import React, { useState } from 'react';
import { registerUser, loginUser } from 'http://localhost:5000/Users_API/users';

const Profile = () => {
  const [isLoginMode, setIsLoginMode] = useState(true); // Toggle between login and signup modes
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [name, setName] = useState(''); // Only used for signup

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (isLoginMode) {
        // Login logic
        const credentials = { email, password };
        const response = await loginUser(credentials);
        if (response.success) {
          console.log('Login successful:', response.user);
        } else {
          console.error('Login failed:', response.message);
        }
      } else {
        // Signup logic
        const newUser = { name, email, password };
        const response = await registerUser(newUser);
        if (response.success) {
          console.log('Registration successful:', response.user);
        } else {
          console.error('Registration failed:', response.message);
        }
      }
    } catch (error) {
      console.error('Error during submission:', error);
    }
  };

  return (
    <div>
      <h1>{isLoginMode ? 'Login' : 'Sign Up'}</h1>
      <form onSubmit={handleSubmit}>
        {!isLoginMode && (
          <input
            type="text"
            placeholder="Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required={!isLoginMode}
          />
        )}
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit">{isLoginMode ? 'Login' : 'Sign Up'}</button>
      </form>
      <button onClick={() => setIsLoginMode((prevMode) => !prevMode)}>
        Switch to {isLoginMode ? 'Sign Up' : 'Login'}
      </button>
    </div>
  );
};

export default Profile;
