import React, { useState, useEffect } from 'react';
import SpeciesCard from '../components/SpeciesCard'; // Assuming SpeciesCard is in the same directory

const Learn = () => {
  const [speciesList, setSpeciesList] = useState([]);

  // Fetch all species on component mount
  useEffect(() => {
    const fetchSpecies = async () => {
      try {
        const response = await fetch('/Species_API/species'); // Adjust API URL if necessary
        if (!response.ok) {
          throw new Error(`Error: ${response.statusText}`);
        }
        const data = await response.json();
        setSpeciesList(data); // Set the fetched species data to state
      } catch (error) {
        console.error('Failed to fetch species:', error);
      }
    };

    fetchSpecies();
  }, []);

  return (
    <div className="learn-container">
      <h1>Learn About Species</h1>
      <div className="species-grid">
        {speciesList.length > 0 ? (
          speciesList.map((species) => (
            <SpeciesCard key={species.id} species={species} />
          ))
        ) : (
          <p>No species data available. Please add some species to the database.</p>
        )}
      </div>
    </div>
  );
};

export default Learn;
